mapper.update:
/*사용자 주소변경*/
UPDATE
    member_addr
SET nickname      = #{nickname},
    roadname_code = #{roadname_code},
    b_code        = #{b_code},
    post          = #{post},
    addr          = #{addr},
    addr_sub      = #{addr_sub}
WHERE addr_seq = #{addr_seq}
  and member_seq = #{member_seq}
